document.addEventListener('DOMContentLoaded', () => {
  const basketButton = document.getElementById('basket-button');
  const basketMenu = document.getElementById('basket-menu');
  const closeButton = document.getElementById('close-button');
  const clearCartButton = document.querySelector('.clear-cart'); // Updated selector for clear cart button

  basketButton.addEventListener('click', () => {
    basketMenu.style.right = '0';
  });

  closeButton.addEventListener('click', () => {
    basketMenu.style.right = '-300px';
  });

  // Function to update the basket content
  function updateBasket(items) {
    // Clear existing quantities to 0 in each section
    document.querySelectorAll('.service-wrapper .card .quantity').forEach(quantitySpan => {
      quantitySpan.textContent = '0';
    });

    // Update quantities based on items in the basket
    items.forEach(item => {
      // Find the corresponding card based on the item's name
      document.querySelectorAll('.card').forEach(card => {
        const planName = card.querySelector('h2').textContent.trim(); // Trim whitespace
        const sectionTitle = card.closest('.service-wrapper').querySelector('h1').textContent.trim(); // Get the h1 heading
        const brand = getBrand(sectionTitle); // Get brand from h1 heading

        // Only update quantities for items that match the brand
        if (item.name === planName && item.brand.toLowerCase() === brand.toLowerCase()) {
          const quantitySpan = card.querySelector('.quantity');
          quantitySpan.textContent = item.quantity;
        }
      });
    });

    // Update the basket content
    const basketContent = document.querySelector('.basket-content');
    basketContent.innerHTML = '';

    items.forEach(item => {
      const itemDiv = document.createElement('div');
      itemDiv.classList.add('basket-item');
      const itemName = item.brand ? `${item.name} (${item.brand})` : item.name; // Include brand in item name if available
      itemDiv.innerHTML = `
        <span class="item-name">${itemName}</span>
        <span class="item-quantity">${item.quantity}</span>
        <button class="clear-item">Remove</button>
      `;
      basketContent.appendChild(itemDiv);

      // Add event listener for the clear item button
      itemDiv.querySelector('.clear-item').addEventListener('click', () => {
        removeFromCart(item);
      });
    });
  }

  // Function to add an item to the cart
  function addToCart(item) {
    let currentItems = getCurrentBasketItems();

    // Get brand from the item or infer it from the section title
    const brand = item.brand ? item.brand.toLowerCase() : getBrandFromItem(item);
    if (brand) {
      const existingItem = currentItems.find(i => i.name === item.name && i.brand.toLowerCase() === brand.toLowerCase());
      if (existingItem) {
        existingItem.quantity += 1; // Increment quantity by 1
      } else {
        currentItems.push({ ...item, quantity: 1, brand: brand }); // Add new item with quantity 1
      }
      localStorage.setItem('cartItems', JSON.stringify(currentItems));
      updateBasket(currentItems);
    }
  }

  // Function to remove an item from the cart
  function removeFromCart(item) {
    let currentItems = getCurrentBasketItems();
    const existingItem = currentItems.find(i => i.name === item.name && i.brand.toLowerCase() === item.brand.toLowerCase());
    if (existingItem && existingItem.quantity > 0) {
      existingItem.quantity -= 1; // Decrement quantity by 1
      if (existingItem.quantity === 0) {
        currentItems = currentItems.filter(i => i.name !== item.name || i.brand.toLowerCase() !== item.brand.toLowerCase()); // Remove item if quantity is zero
      }
      localStorage.setItem('cartItems', JSON.stringify(currentItems));
      updateBasket(currentItems);
    }
  }

  // Function to clear the cart
  function clearCart() {
    localStorage.removeItem('cartItems'); // Remove cart items from localStorage
    updateBasket([]); // Update UI to clear the basket content
  }

  // Event delegation for plus and minus buttons
  document.addEventListener('click', event => {
    const plusButton = event.target.closest('.plus');
    const minusButton = event.target.closest('.minus');

    if (plusButton) {
      const card = plusButton.closest('.card');
      const planName = card.querySelector('h2').textContent.trim(); // Trim whitespace
      const brand = getBrandFromCard(card);
      addToCart({ name: planName, brand: brand });
    }

    if (minusButton) {
      const card = minusButton.closest('.card');
      const planName = card.querySelector('h2').textContent.trim(); // Trim whitespace
      const brand = getBrandFromCard(card);
      removeFromCart({ name: planName, brand: brand });
    }
  });

  // Event listener for the clear cart button
  clearCartButton.addEventListener('click', () => {
    clearCart();
  });

  // Function to extract brand from h1 section title
  function getBrand(sectionTitle) {
    if (sectionTitle.toLowerCase().includes("gap")) {
      return "gap";
    } else if (sectionTitle.toLowerCase().includes("fone")) {
      return "fone";
    } else if (sectionTitle.toLowerCase().includes("flipper")) {
      return "flipper";
    }
    return ""; // Default return if no match
  }

  // Function to get brand from item object
  function getBrandFromItem(item) {
    if (item.name.toLowerCase().includes("gap")) {
      return "gap";
    } else if (item.name.toLowerCase().includes("fone")) {
      return "fone";
    } else if (item.name.toLowerCase().includes("flipper")) {
      return "flipper";
    }
    return ""; // Default return if no match
  }

  // Function to get brand from card element
  function getBrandFromCard(card) {
    const sectionTitle = card.closest('.service-wrapper').querySelector('h1').textContent.trim();
    return getBrand(sectionTitle);
  }

  // Function to get current basket items from localStorage
  function getCurrentBasketItems() {
    return JSON.parse(localStorage.getItem('cartItems')) || [];
  }

  // Function to load cart items from localStorage when the page loads
  function loadCartItems() {
    const storedItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    updateBasket(storedItems);
  }

  // Example usage: Update cart items when the page loads
  loadCartItems(); // Load items from localStorage when the page loads
});

