@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600&display=swap');

/* Reset and General Styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Poppins", sans-serif;
}

body {
  background: url('https://img.freepik.com/free-photo/optical-fiber-background_23-2149301562.jpg') no-repeat center fixed;
  background-size: cover;
  min-height: 100vh;
  margin: 0;
  padding: 0;
  color: white;
}

/* Header */
.header {
  width: 100%;
  padding: 15px 12.5%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1000;
  position: relative; 
}

.navbar {
  display: flex;
  align-items: center;
}

.navbar a {
  font-size: 16px;
  color: #e4e4e4;
  text-decoration: none;
  font-weight: 500;
  margin-right: 20px;
  padding: 10px 15px;
  position: relative;
}

.navbar a::after {
  content: '';
  position: absolute;
  left: 0;
  bottom: -6px;
  width: 100%;
  height: 2px;
  background: #e4e4e4;
  border-radius: 5px;
  transform: translateY(10px);
  opacity: 0;
  transition: .5s;
}

.navbar a:hover::after {
  transform: translateY(0);
  opacity: 1;
}

.search-bar {
  width: 250px;
  height: 45px;
  background: transparent;
  border: 2px solid #e4e4e4;
  border-radius: 6px;
  display: flex;
  align-items: center;
  position: fixed; 
  top: 15px; 
  right: 12.5%; 
}

.search-bar input {
  width: calc(100% - 40px); 
  background: transparent;
  border: none;
  outline: none;
  font-size: 16px;
  color: #e4e4e4;
  padding-left: 10px;
}

.search-bar input::placeholder {
  color: #e4e4e4;
}

.search-bar button {
  width: 40px;
  height: 100%;
  background: transparent;
  border: none;
  outline: none;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
}

.search-bar button i {
  font-size: 22px;
  color: #e4e4e4;
}

/* About Section */
.about {
  padding: 50px 12.5% 20px; /* Reduced padding */
  text-align: center;
  color: white;
}

.about h1 {
  font-size: 5rem;
  -webkit-text-stroke-width: 2px;
  -webkit-text-stroke-color: transparent;
  letter-spacing: 4px;
  background-color: rgb(4, 52, 83);
  background: linear-gradient(8deg, rgba(8, 52, 83, 1) 0%, rgba(0, 230, 173, 1) 41%, rgba(41, 17, 45, 1) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  position: relative;
  margin-bottom: 20px;
}

.about h1::after {
  content: "";
  position: absolute;
  top: 100%;
  left: 10%;
  height: 8px;
  width: 80%;
  border-radius: 8px;
  background-color: rgba(255, 255, 255, 0.05);
}

.about p {
  font-size: 1rem;
  color: white;
  max-width: 800px;
  margin: 0 auto;
}

.about-info {
  margin: 2rem 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: left;
}

.about-img {
  width: 20rem;
  height: 20rem;
  overflow: hidden;
  border-radius: 10px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.3);
}

.about-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.4s ease;
}

.about-img img:hover {
  transform: scale(1.1);
}

.about-info p {
  margin: 0 2rem 0 2rem;
  text-align: justify;
}

button {
  border: none;
  outline: 0;
  padding: 10px;
  margin: 2rem;
  font-size: 1rem;
  color: #fff;
  background-color: #333;
  text-align: center;
  cursor: pointer;
  width: 15rem;
  border-radius: 4px;
}

button:hover {
  background-color: #555;
}

button a {
  text-decoration: none;
  color: inherit;
  display: block;
  width: 100%;
  height: 100%;
}

/* Team Section */
.team {
  padding: 30px 12.5% 20px; /* Reduced padding */
  text-align: center;
}

.team h1 {
  font-size: 2.5rem;
  margin-bottom: 20px;
}

.team-cards {
  display: flex; /* Use flexbox for the team cards */
  flex-wrap: wrap; /* Wrap cards to the next line on smaller screens */
  gap: 20px;
  justify-content: center;
  margin-top: 40px;
}

.card {
  height: 450px; /* Adjusted height */
  width: 300px; /* Fixed width */
  background-color: #1c2335; /* Dark background color */
  border: 2px solid transparent; /* Transparent border initially */
  border-radius: 10px;
  overflow: hidden;
  position: relative;
  transition: transform 0.3s, border-color 0.3s, background-color 0.3s;
}

.card:hover {
  transform: translateY(-8px); /* Move up slightly on hover */
  border-color: #00ff37; /* Green border color on hover */
  background-color: transparent; /* Transparent background on hover */
}

.card::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.1); /* Blurred white background */
  transition: background-color 0.3s;
}

.card:hover::after {
  background-color: rgba(255, 255, 255, 0); /* Lighter blurred background on hover */
}

.card-img {
  width: 100%;
  height: 60%; /* Adjusted height */
  position: relative;
  overflow: hidden;
}

.card-img::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5); /* Semi-transparent overlay */
  transition: background 0.3s;
}

.card:hover .card-img::before {
  background: rgba(0, 0, 0, 0.3); /* Change transparency on hover */
}

.card-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  position: relative;
  z-index: 1;
}

.card-info {
  padding: 10px;
  text-align: center;
}

.card-info h2 {
  font-size: 1.5rem;
  margin: 10px 0;
}

.card-info p {
  margin: 5px 0;
}

/* Footer */
footer {
  background-color: #333;
  color: white;
  text-align: center;
  padding: 20px 0;
}

@media screen and (max-width: 900px) {
  .header {
    padding: 15px 5%;
  }

  .navbar {
    display: block;
    text-align: center;
    padding: 10px 0;
  }

  .navbar a {
    margin: 0 10px;
  }

  .about h1 {
    font-size: 2rem;
  }

  .about p {
    font-size: 0.9rem;
  }

  .about-info {
    flex-direction: column;
    text-align: center;
  }

  .about-img {
    width: 60%;
    height: 60%;
    margin-bottom: 1rem;
  }

  .about-info p {
    margin: 1rem 2rem;
  }

  button {
    margin: 1rem 2rem;
    width: 10rem;
  }

  .team {
    margin: 0 1rem;
  }

  .team-cards {
    flex-direction: column; /* Single column layout on smaller screens */
  }
}

