  flex-direction: column;
  align-items: center;
}

h1 {
  color: #fff;
  font-size: 5rem;
  -webkit-text-stroke-width: 2px;
  -webkit-text-stroke-color: transparent;
  letter-spacing: 4px;
  background-color: rgb(4, 52, 83);
  background: linear-gradient(8deg, rgba(8, 52, 83, 1) 0%, rgba(0, 230, 173, 1) 41%, rgba(41, 17, 45, 1) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  position: relative;
  margin-bottom: 20px;
}

h1::after {
  content: "";
  position: absolute;
  top: 100%;
  left: 10%;
  height: 8px;
  width: 80%;
  border-radius: 8px;
  background-color: rgba(255, 255, 255, 0.05);
}

h1 span {
  position: absolute;
  top: 100%;
  left: 10%;
  height: 8px;
  width: 8px;
  border-radius: 50%;
  background-color: #72e2ae;
  animation: anim 5s linear infinite;
}

@keyframes anim {
  95% {
    opacity: 1;
  }
  100% {
    opacity: 0;
    left: 88%;
  }
}

.cards {
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* Changed to 3 columns */
  gap: 30px;
  justify-content: center;
  align-items: center;
  margin-top: 80px;
}

.card {
  height: 350px;
  background-color: #1c2335;
  padding: 10% 0; /* Adjusted padding */
  border: 0.2px solid rgba(114, 226, 174, 0.2);
  border-radius: 8px;
  transition: 0.6s;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  text-align: center; /* Center align text */
}

.card i {
  color: #72e2ae;
  margin-top: 30px;
  margin-bottom: 10px; /* Adjusted margin */
  font-size: 4rem; /* Adjusted font size */
}

.card h2 {
  color: #fff;
  font-size: 20px; /* Adjusted font size */
  font-weight: 600;
  letter-spacing: 1px;
}

.card p {
  color: #e4e4e4;
  font-size: 16px;
  margin-bottom: 10px;
}

.card .info {
  color: #08a1b2; /* Adjusted info color */
  font-size: 18px;
  font-weight: 500;
  margin-bottom: 10px;
}

.card .price {
  color: #08a1b2; /* Price color remains unchanged */
  font-size: 24px;
  font-weight: 600;
  margin-top: auto; /* Push price to the bottom */
}

.card:hover {
  background-color: transparent;
  transform: translateY(-8px);
  border-color: #00ff37;
}

@media screen and (max-width: 900px) {
  .cards {
    grid-template-columns: repeat(1, 1fr); /* Change to 1 column for smaller screens */
  }

  h1 {
    font-size: 3.5rem;
  }
}

/* Adjusted Section Title Styles */
.section-title {
  color: #fff;
  font-size: 3rem;
  margin-bottom: 20px;
  text-align: center;
  margin-top: 20px; /* Adjusted margin */
}

