@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600&display=swap');

/* Reset and basic styling */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Poppins", sans-serif;
  background: url('https://img.freepik.com/free-photo/optical-fiber-background_23-2149301562.jpg') no-repeat center fixed;
  background-size: cover;
  min-height: 100vh;
  margin: 0;
  padding: 0;
}

.header {
  width: 100%;
  padding: 15px 12.5%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1000;
}

.navbar a {
  font-size: 16px;
  color: #e4e4e4;
  text-decoration: none;
  font-weight: 500;
  margin-right: 20px;
  padding: 10px 15px;
  position: relative;
}

.navbar a::after {
  content: '';
  position: absolute;
  left: 0;
  bottom: -6px;
  width: 100%;
  height: 2px;
  background: #e4e4e4;
  border-radius: 5px;
  transform: translateY(10px);
  opacity: 0;
  transition: .5s;
}

.navbar a:hover::after {
  transform: translateY(0);
  opacity: 1;
}

.search-bar {
  width: 250px;
  height: 45px;
  background: transparent;
  border: 2px solid #e4e4e4;
  border-radius: 6px;
  display: flex;
  align-items: center;
}

.search-bar input {
  width: 100%;
  background: transparent;
  border: none;
  outline: none;
  font-size: 16px;
  color: #e4e4e4;
  padding-left: 10px;
}

.search-bar input::placeholder {
  color: #e4e4e4;
}

.search-bar button {
  width: 40px;
  height: 100%;
  background: transparent;
  border: none;
  outline: none;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
}

.search-bar button i {
  font-size: 22px;
  color: #e4e4e4;
}

.basket-icon {
  font-size: 22px;
  color: #e4e4e4;
  cursor: pointer;
  position: relative;
}

.basket-menu {
  position: fixed;
  top: 0;
  right: -300px;
  width: 300px;
  height: 100%;
  background: rgba(0, 0, 0, 0.9);
  color: #e4e4e4;
  z-index: 2000;
  transition: right 0.3s ease;
}

.basket-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid #e4e4e4;
}

.basket-header h2 {
  font-size: 1.5rem;
}

.basket-header button {
  background: none;
  border: none;
  color: #e4e4e4;
  font-size: 1.5rem;
  cursor: pointer;
}

.basket-content {
  padding: 20px;
  overflow-y: auto;
}

.container {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding: 100px 0 50px; /* Add padding to the top for spacing */
}

.service-wrapper {
  width: 75%;
  padding: 5% 8%;
  color: #e4e4e4;
  background: rgba(0, 0, 0, 0.6);
  border-radius: 10px;
  margin-top: 40px; /* Adjusted margin */
}

