.checkout-button {
  background-color: #4CAF50; /* Green color matching your website */
  color: white;
  border: none;
  padding: 12px 24px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin-top: 20px; /* Adjust margin to create space between items and button */
  cursor: pointer;
  border-radius: 8px; /* Rounded corners for modern look */
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2); /* Shadow for depth */
  width: 100%; /* Button width covers entire container */
  max-width: 300px; /* Example max-width to limit button size */
}

.checkout-button:hover {
  background-color: #45a049; /* Darker shade on hover */
}


/* Styling for basket item remove button */
.basket-item .clear-item {
  background-color: #ff6347; /* Red color */
  color: white;
  border: none;
  padding: 5px 10px;
  cursor: pointer;
  border-radius: 5px;
  margin-left: 10px; /* Adjust spacing */
}

.basket-item .clear-item:hover {
  background-color: #ff4837; /* Darker shade on hover */
}

/* Styling for clear cart button */
.clear-cart {
  background-color: #ff6347; /* Red color */
